mapper.update:
update t_role
set DESCRIPTION = #{description,jdbcType=VARCHAR},
GEN_TIME = #{genTime,jdbcType=TIMESTAMP},
ROLE_NAME = #{roleName,jdbcType=VARCHAR},
ROLE_NAME_LOCAL = #{roleNameLocal,jdbcType=VARCHAR}
where ID = #{id,jdbcType=BIGINT}

mapper.select:
select pri.id from t_privilege as pri
left join t_ref_privilege_role as ref on ref.pri_id = pri.id
left join t_role as r on r.id = ref.role_id
where pri.pri_type=#{pritype,jdbcType=BIGINT} and r.id=#{roleid,jdbcType=BIGINT}

     and pri.pri_symbol = #{symbol,jdbcType=BIGINT}
